  supportedTransitProviders,
} from './transitProviders'
import OreIdContext from '../core/IOreidContext'
import Helpers from '../utils/helpers'
import {
  AccountName,
  AuthProvider,
  ChainAccount,
  ChainNetwork,
  ChainPlatformType,
  ConnectToTransitProviderParams,
  DiscoverOptions,
  ExternalWalletInterface,
  ExternalWalletType,
  LoginWithWalletOptions,
  PermissionName,
  PublicKey,
  SettingChainNetworkHost,
  SetupTransitWalletParams,
  SignatureProviderArgs,
  SignatureProviderSignResult,
  SignStringParams,
  TransactionData,
  TransitAccountInfo,
  TransitWallet,
  WalletPermission,
} from '../models'
import { TransitDiscoveryOptions, TransitWalletAccessContext } from '.'
import { User } from '../user/user'

type ConnectToTransitProviderResult = {
  isLoggedIn?: boolean
  chainAccount?: ChainAccount
  permissions?: [{ name: PermissionName; publicKey: PublicKey }]
  transitWallet?: TransitWallet
  provider?: ExternalWalletType
}

export default class TransitHelper {
  constructor(args: { oreIdContext: OreIdContext; user: User }) {
    this._oreIdContext = args.oreIdContext
    this._user = args.user
    this.transitAccessContexts = {}
  }

  _oreIdContext: OreIdContext

  _user: User

  transitAccessContexts: { [key: string]: TransitWalletAccessContext }

  /** Verifies that all plugins provided work (can be constructed)
   *  Stores a list of the installed providerNames (mapped to AuthProvider) for all working plugins in transitProvidersInstalled
   */
  async installTransitProviders(eosTransitWalletProviders: MakeWalletProviderFn[]) {
    // Executes each provider's contructor to verify it's working
    // stores all the providerName's for all plugins into transitProvidersInstalled array
    this._oreIdContext.transitProvidersInstalled = (eosTransitWalletProviders || [])
      .map(makeWalletProvider => {
        try {
          // if there is an error while initiating a provider dont break the whole process.
          return makeWalletProvider(null)
        } catch (e) {
          console.log(`Couldn't initiate a wallet provider. ${e}`)
          return null
        }
      }) // instantiate the provider with null network so we can get the id
      .filter(walletProvider => walletProvider && true)
      .map(walletProvider => {
        return getTransitProviderAttributesByProviderId(walletProvider.id).providerName
      })
  }

  /** Inialize EOS Transit wallet provider and return TransitWallet instance */
  async setupTransitWallet({ walletType, chainNetwork }: SetupTransitWalletParams): Promise<TransitWallet> {
    this.assertHasProviderInstalled(walletType, ExternalWalletInterface.Transit)
    this.assertProviderValidForChainNetwork(walletType, chainNetwork)
    const { providerId } = getTransitProviderAttributes(walletType)
    const chainContext = await this.getOrCreateTransitAccessContext(chainNetwork)
    const transitProvider = chainContext.getWalletProviders().find(wp => wp.id === providerId)
    const transitWallet = chainContext.initWallet(transitProvider)
    await transitWallet.connect()
    await this.waitWhileWalletIsBusy(transitWallet, walletType)
    return transitWallet
  }

  /** Returns network config (url, port, etc.) for specified chainNetwork */
  private async getChainNetworkNextworkConfig(chainNetwork: ChainNetwork): Promise<SettingChainNetworkHost> {
    const networkSettings = await this._oreIdContext.getChainNetworkSettings(chainNetwork)
    if (!networkSettings) {
      throw new Error(`Invalid chain network: ${chainNetwork}.`)
    }
    const { chainId, host, port, protocol } = networkSettings?.hosts[0] || {} // using first host
    return { host, port, protocol, chainId }
  }

  /** Creates an EOS Transit WalletContent for the specified network and plugins
   *  Caches the context for future calls to this function */
  private async getOrCreateTransitAccessContext(chainNetwork: ChainNetwork) {
    const { appName, eosTransitWalletProviders = [] } = this._oreIdContext.options
    if (this.transitAccessContexts[chainNetwork]) {
      return this.transitAccessContexts[chainNetwork]
    }
    const networkConfig = await this.getChainNetworkNextworkConfig(chainNetwork)
    const isNotEosNetwork = await this.isNotEosNetwork(chainNetwork)
    const walletContext = initAccessContext({
      appName: appName || 'missing appName',
      network: networkConfig,
      walletProviders: eosTransitWalletProviders,
      isNotEosNetwork, // Tells eos-transit to not use EOS specific rpc calls
    })
    // cache for future use
    this.transitAccessContexts[chainNetwork] = walletContext
    return walletContext
  }

  // For Scatter: chainAccount is needed since login will try to use the default account (in scatter
  // and it wil fail to sign the transaction
  /** Handles the call to connect() function on the Transit provider */
  async connectToTransitProvider({
    walletType,
    chainNetwork = ChainNetwork.EosMain,
    chainAccount = null,
  }: ConnectToTransitProviderParams): Promise<ConnectToTransitProviderResult> {
    let response: ConnectToTransitProviderResult
    this.assertHasProviderInstalled(walletType, ExternalWalletInterface.Transit)
    this.assertProviderValidForChainNetwork(walletType, chainNetwork)
    try {
      const transitWallet: TransitWallet = await this.setupTransitWallet({ walletType, chainNetwork })
      response = { transitWallet }
      // some providers require login flow to connect (usually this means connect() does nothing but login selects an account)
      if (getTransitProviderAttributes(walletType).requiresLogin) {
        // if connected, but not authenticated, then login
        if (!transitWallet.authenticated) {
          await this.loginToTransitProvider(transitWallet, walletType, chainNetwork, chainAccount)
        }
      }

      // If connecting also performs login
      // return login results or throw error
      if (transitWallet.connected) {
        // if wallet has an account (by logging in), add it to OREID account add account info to response
        if (transitWallet.authenticated && transitWallet.auth) {
          await this.updateOreAccountPermissionsfromTransitWalletAuth(transitWallet, walletType)
          const { accountName, permission, publicKey } = transitWallet.auth
          response = {
            isLoggedIn: true,
            chainAccount: accountName,
            permissions: [{ name: permission, publicKey }], // todo: add parent permission when available
            transitWallet,
            provider: walletType,
          }
        }
      } else {
        let errorString = `${walletType} not connected!`
        const { hasError, errorMessage } = transitWallet
        if (hasError) {
          errorString += ` Error: ${errorMessage}`
        }
        throw new Error(errorString)
      }
    } catch (error) {
      const errMsg = `Failed to connect to ${walletType} on ${chainNetwork}. ${error?.message || ''}`
      console.log(`connectToTransitProvider:${errMsg}`, error)
      throw new Error(errMsg)
    } finally {
      this._oreIdContext.setIsBusy(false)
    }

    return response
  }

  /** Handles the call to login() function on the Transit provider
   *  If required by provider, calls discover() and/or logout() before calling login()
   *  IMPORTANT: use loginToTransitProvider() instead of this function */
  private async doTransitProviderLogin(
    transitWallet: TransitWallet,
    chainAccount: ChainAccount,
    chainNetwork: ChainNetwork,
    walletType: ExternalWalletType,
    retryCount = 0,
  ) {
    let info: TransitAccountInfo
    this.assertHasProviderInstalled(walletType, ExternalWalletInterface.Transit)
    this.assertProviderValidForChainNetwork(walletType, chainNetwork)
    // we should store the index for ledger in the db and pass it along
    // but for now we need to discover the ledger index
    const { requiresDiscoverToLogin } = getTransitProviderAttributes(walletType)
    if (requiresDiscoverToLogin) {
      // we have to discover on ledger since we don't know the index of the account
      //
      const discoveryData = await transitWallet.discover(this.discoverOptionsForProvider(walletType))
      const foundData = this.findAccountInDiscoverData(discoveryData, chainAccount)
      if (foundData) {
        info = await transitWallet.login(chainAccount, foundData.authorization)
      } else {
        throw new Error(`Account ${chainAccount} not found in wallet`)
      }
    } else {
      info = await transitWallet.login(chainAccount)
    }

    if (retryCount > 2) {
      // don't get stuck in a loop, let the transaction fail so the user will figure it out
      return null
    }

    const { accountName: transitAccountName } = transitWallet?.auth || {}

    if (chainAccount && transitAccountName !== chainAccount) {
      // keep trying until the user logs in with the correct wallet
      // in scatter, it will ask you to choose an account if you logout and log back in
      // we could also call discover and login to the matching account and that would avoid a step
      await transitWallet.logout()
      this.doTransitProviderLogin(transitWallet, chainAccount, chainNetwork, walletType, retryCount + 1)
    }
    return info
  }

  /** Login using the wallet provider */
  async loginWithTransitProvider(loginOptions: LoginWithWalletOptions) {
    const { provider, chainAccount, chainNetwork } = loginOptions
    // Connect to Provider
    const walletType = Helpers.mapAuthProviderToWalletType(provider)
    const response = await this.connectToTransitProvider({ walletType, chainAccount, chainNetwork })
    const wallet = response?.transitWallet
    // Login if needed - if not logged-in by connectToTransitProvider, then call login explicitly
    if (!wallet?.auth) {
      await this.loginToTransitProvider(wallet, provider, chainNetwork, chainAccount)
      await this.updateOreAccountPermissionsfromTransitWalletAuth(wallet, provider)
    }
    return response
  }

  /** Handles the call to login() function on the Transit provider */
  private async loginToTransitProvider(
    transitWallet: TransitWallet,
    walletType: ExternalWalletType,
    chainNetwork: ChainNetwork,
    chainAccount: ChainAccount = null,
  ) {
    this.assertHasProviderInstalled(walletType, ExternalWalletInterface.Transit)
    this.assertProviderValidForChainNetwork(walletType, chainNetwork)
    try {
      // if the default login is for a different account
      await this.doTransitProviderLogin(transitWallet, chainAccount, chainNetwork, walletType)
    } catch (error) {
      const { message = '' } = error
      if (message.includes('unknown key (boost::tuples::tuple')) {
        throw new Error(`The account selected by the wallet for login isn't on the ${chainNetwork} chain`)
      } else {
        throw error
      }
    } finally {
      await this.waitWhileWalletIsBusy(transitWallet, walletType)
    }
  }

  /** Throw if the required plug-in is not installed */
  assertHasProviderInstalled(provider: ExternalWalletType, providerType: ExternalWalletInterface) {
    if (providerType === ExternalWalletInterface.Transit) {
      if (!this.hasTransitProvider(provider)) {
        throw Error(`Transit provider:"${provider}" not installed. Please pass it in via eosTransitWalletProviders.`)
      }
    }
  }

  /** Discovers keys in a wallet provider.
   *  Any new keys discovered in wallet are added to user's ORE ID record.
   *  If the provider doesnt support a discover() function, and requiresLogoutLoginToDiscover == true, attempts a logout then login instead.
   */
  async discoverWithTransit(discoverOptions: DiscoverOptions) {
    const { walletType, chainNetwork = ChainNetwork.EosMain, oreAccount, discoveryPathIndexList } = discoverOptions
    this.assertProviderValidForChainNetwork(walletType, chainNetwork)
    let result = null
    if (this.canDiscover(walletType)) {
      result = await this.discoverCredentialsInTransitWallet(
        chainNetwork,
        walletType,
        oreAccount,
        discoveryPathIndexList,
      )
    } else {
      // if provider doesn't support a discover function, we can use login to retrieve a single account/key instead
      const transitWallet = await this.setupTransitWallet({ walletType, chainNetwork })
      if (this.requiresLogoutLoginToDiscover(walletType)) {
        await transitWallet.logout()
        await transitWallet.login()
        this.updateOreAccountPermissionsfromTransitWalletAuth(transitWallet, walletType)
      } else {
        console.log('Discover not working for walletType: ', walletType)
      }
    }

    return result
  }

  /** Discover all accounts (and related permissions) in the wallet and add them to ORE ID
   * Note: Most wallets don't support discovery */
  private async discoverCredentialsInTransitWallet(
    chainNetwork: ChainNetwork,
    walletType: ExternalWalletType,
    oreAccount: AccountName,
    discoveryPathIndexList: number[],
  ) {
    let accountsAndPermissions: WalletPermission[] = []

    try {
      const transitWallet = await this.setupTransitWallet({ walletType, chainNetwork })
      this._oreIdContext.setIsBusy(true)
      const discoveryData = await transitWallet.discover(
        this.discoverOptionsForProvider(walletType, discoveryPathIndexList),
      )
      // this data looks like this: keyToAccountMap[accounts[{account,permission}]] - e.g. keyToAccountMap[accounts[{'myaccount':'owner','myaccount':'active'}]]
      const credentials = discoveryData.keyToAccountMap
      // for each entry in the array, add permission to ore account if not already present
      await Helpers.asyncForEach(credentials, async credential => {
        const { accounts = [], key: publicKey } = credential
        // ethereum may not have a public key - dont save if missing
        if (accounts.length > 0 && !!publicKey) {
          const [{ account, authorization }] = accounts // get first item in array
          const permissions: WalletPermission[] = [
            {
              account,
              publicKey,
              name: authorization,
              parent: null,
            },
          ]
          // Get the chainNetwork from the transitWallet - in case the wallet provider switches networks somehow
          const transitChainNetwork = await this.getChainNetworkFromTransitWallet(transitWallet)
          await this._user?.addWalletPermissionsToOreIdAccount({
            chainAccount: account,
            chainNetwork: transitChainNetwork,
            permissions,
            walletType,
          })
          accountsAndPermissions = accountsAndPermissions.concat(permissions)
        }
      })
    } finally {
      this._oreIdContext.setIsBusy(false)
    }
    // return a list of account names and related permissions found
    return accountsAndPermissions
  }

  /** Discover options composed for specific provider */
  private discoverOptionsForProvider(
    provider: ExternalWalletType,
    pathIndexListParam: number[] = null,
  ): TransitDiscoveryOptions {
    let pathIndexList
    let keyLookupFunc
    const walletProviderType = Helpers.mapAuthProviderToWalletType(provider)
    if (this.hasTransitProvider(walletProviderType)) {
      const walletProvider = Helpers.mapAuthProviderToWalletType(provider)
      const { defaultDiscoveryPathIndexList, discoveryKeyLookupFunc } = getTransitProviderAttributes(walletProvider)
      pathIndexList = pathIndexListParam || defaultDiscoveryPathIndexList || []
      keyLookupFunc = discoveryKeyLookupFunc // optional
    }

    const response: TransitDiscoveryOptions = { pathIndexList }
    if (keyLookupFunc) {
      response.keyLookupFunc = keyLookupFunc
    }

    return response
  }

  private findAccountInDiscoverData(discoveryData: any, chainAccount: ChainAccount) {
    const result = discoveryData.keyToAccountMap.find((data: any) => {
      return data.accounts.find((acct: any) => {
        return acct.account === chainAccount
      })
    })

    if (result) {
      let authorization = 'active'

      // could active not exist?  If not, then just get first permission
      // this may be completely unecessary. remove if so.
      const active = result.accounts.find((acct: any) => {
        return acct.authorization === 'active'
      })

      if (!active) {
        const [first] = result.accounts

        if (first) {
          // eslint-disable-next-line prefer-destructuring
          authorization = first.authorization
        }
      }

      return { index: result.index, key: result.key, authorization }
    }

    return null
  }

  /** sign with a Transit wallet */
  async signWithTransitProvider(transactionData: TransactionData, transitProvider: ExternalWalletType) {
    let signedTransaction: SignatureProviderSignResult
    const { chainNetwork, chainAccount } = transactionData
    const walletType = Helpers.mapAuthProviderToWalletType(transitProvider)
    this.assertHasProviderInstalled(walletType, ExternalWalletInterface.Transit)
    this.assertProviderValidForChainNetwork(walletType, chainNetwork)
    // connect to wallet
    const { transitWallet } = await this.connectToTransitProvider({
      walletType,
      chainNetwork,
      chainAccount,
    })

    try {
      // sign with transit wallet
      this._oreIdContext.setIsBusy(true)
      const { chainType } = getTransitProviderAttributes(walletType)
      // EOS - use eosJS to sign (eosApi.transact)
      if (chainType === ChainPlatformType.eos) {
        signedTransaction = await this.signTransactionWithTransitAndEosSDK(transactionData, transitWallet)
      } else if (chainType === ChainPlatformType.algorand) {
        // Other chains - use sign function on walletProvider
        signedTransaction = await this.signTransactionWithTransitAndAlgorandSDK(transactionData, transitWallet)
      } else if (chainType === ChainPlatformType.ethereum) {
        // Ethereum - use sign function on ethereum walletProvider
        signedTransaction = await this.signTransactionWithTransitAndEthereumSDK(transactionData, transitWallet)
      } else {
        throw new Error(`signWithTransitProvider doesnt support chain type: ${chainType}`)
      }
    } catch (error) {
      console.error(error)
      throw error
    } finally {
      this._oreIdContext.setIsBusy(false)
    }
    // Convert serializedTransaction from UInt8Array to Buffer
    // i.e. when stringified change from: '{\"0\":129,\"1\":163'} to {"type":"Buffer","data":[129,163]}
    if (signedTransaction.serializedTransaction) {
      signedTransaction.serializedTransaction = Buffer.from(signedTransaction.serializedTransaction)
    }
    return { signedTransaction }
  }

  async signStringWithTransitProvider({ walletType, chainNetwork, string, message }: SignStringParams) {
    this.assertHasProviderInstalled(walletType, ExternalWalletInterface.Transit)
    this.assertProviderValidForChainNetwork(walletType, chainNetwork)
    const { transitWallet } = await this.connectToTransitProvider({ walletType, chainNetwork })
    try {
      this._oreIdContext.setIsBusy(true)
      const response = await transitWallet.signArbitrary(string, message)
      return { signedString: response }
    } catch (error) {
      console.error(error)
      throw error
    } finally {
      this._oreIdContext.setIsBusy(false)
    }
  }

  /** sign transaction using EOS SDK .transact function */
  private async signTransactionWithTransitAndEosSDK(transactionData: TransactionData, transitWallet: Wallet) {
    const { expireSeconds, transaction } = transactionData
    const { broadcast } = transactionData?.signOptions || {}
    const { signatures, serializedTransaction } = await transitWallet.eosApi.transact(
      {
        actions: [transaction],
      },
      {
        broadcast,
        blocksBehind: 3,
        expireSeconds: expireSeconds || 60,
      },
    )
    await this.callDiscoverAfterSign(transactionData)
    return { signatures, serializedTransaction }
  }

  /** sign transaction using Algorand SDK */
  private async signTransactionWithTransitAndAlgorandSDK(transactionData: TransactionData, transitWallet: Wallet) {
    const { chainNetwork, transaction } = transactionData
    // Other chains - use sign function on walletProvider
    const networkConfig = await this.getChainNetworkNextworkConfig(chainNetwork)
    const signParams: SignatureProviderArgs = {
      chainId: networkConfig.chainId, // Chain transaction is for
      requiredKeys: null, // not used by Algorand signatureProvider
      serializedTransaction: msgPackEncode(transaction), // Transaction to sign
      abis: null, // not used by Algorand signatureProvider
    }
    const { signatures, serializedTransaction } = await transitWallet.provider.signatureProvider.sign(signParams)
    await this.callDiscoverAfterSign(transactionData)
    return { signatures, serializedTransaction }
  }

  /** sign transaction using ethereum web3 SDK */
  private async signTransactionWithTransitAndEthereumSDK(transactionData: TransactionData, transitWallet: Wallet) {
    const { chainNetwork, transaction } = transactionData
    // Other chains - use sign function on walletProvider
    const networkConfig = await this.getChainNetworkNextworkConfig(chainNetwork)
    const signParams: SignatureProviderArgs = {
      chainId: networkConfig.chainId, // Chain transaction is for
      requiredKeys: null, // not used by Ethereum signatureProvider
      serializedTransaction: msgPackEncode(transaction), // Transaction to sign
      abis: null, // not used by Ethereum signatureProvider
    }
    const { signatures, serializedTransaction } = await transitWallet.provider.signatureProvider.sign(signParams)
    await this.callDiscoverAfterSign(transactionData)
    return { signatures, serializedTransaction }
  }

  /** Determine the chainNetwork from the transitWallet context */
  async getChainNetworkFromTransitWallet(transitWallet: TransitWallet) {
    const { chainId } = transitWallet?.ctx?.network || {}
    if (!chainId) {
      return null
    }
    const networks = await this._oreIdContext.getAllChainNetworkSettings()
    return networks.find(net => net.hosts.find(host => host.chainId === chainId))?.network
  }

  /** Add the account selected in the transitWallet to the ORE account's list of account/permissions */
  async updateOreAccountPermissionsfromTransitWalletAuth(transitWallet: TransitWallet, walletType: ExternalWalletType) {
    if (!transitWallet?.connected || !transitWallet?.auth) {
      return
    }
    const { accountName, permission, publicKey } = transitWallet.auth
    // abort silently if account is missing some info - some chains/wallets (e.g. ethereum) dont provide the public key, so we can't add the perm here
    if (!accountName || !permission || !publicKey) {
      return
    }
    const permissions: WalletPermission[] = [{ name: permission, publicKey }] // todo: add parent permission when available
    // Get the chainNetwork from the transitWallet - in case the wallet provider switches networks somehow
    const transitChainNetwork = await this.getChainNetworkFromTransitWallet(transitWallet)
    if (transitChainNetwork) {
      await this._user?.updatePermissionsIfNecessary({
        chainAccount: accountName,
        chainNetwork: transitChainNetwork,
        permissions,
        walletType,
      })
    }
  }

  isTransitProvider(provider: AuthProvider | ExternalWalletType) {
    const walletProviderType = Helpers.mapAuthProviderToWalletType(provider)
    if (!walletProviderType) return false
    return supportedTransitProviders.includes(walletProviderType)
  }

  /** Whether this Eos Transit provider was installed upon instantiation */
  hasTransitProvider(walletType: ExternalWalletType): boolean {
    return this._oreIdContext.transitProvidersInstalled.includes(walletType)
  }

  /** Throw if the provider doesnt support the specified chainNetwork */
  async assertProviderValidForChainNetwork(walletType: ExternalWalletType, chainNetwork: ChainNetwork) {
    const { chainType } = getTransitProviderAttributes(walletType)
    const networks = await this._oreIdContext.getAllChainNetworkSettings()
    const isValid = !!networks.find(n => n.network === chainNetwork && n.type === chainType)
    if (!isValid) {
      throw Error(
        `External Wallet Type: ${walletType} doesnt support chainNetwork ${chainNetwork}. Hint: It supports networks of type ${chainType}.`,
      )
    }
  }

  private async waitWhileWalletIsBusy(transitWallet: TransitWallet, walletType: ExternalWalletType) {
    while (transitWallet.inProgress) {
      this._oreIdContext.setIsBusy(true)
      // todo: add timeout
      // eslint-disable-next-line no-await-in-loop
      await Helpers.sleep(250)
      console.log(`connecting to ${walletType} via eos-transit wallet in progress:`, transitWallet.inProgress)
    }
    this._oreIdContext.setIsBusy(false)
  }

  /** Discovers keys in a wallet provider.
   *  Any new keys discovered in wallet are added to user's ORE ID record.
   *  If the provider doesnt support a discover() function, and requiresLogoutLoginToDiscover == true, attempts a logout then login instead.
   */
  async discover(discoverOptions: DiscoverOptions) {
    return this.discoverWithTransit(discoverOptions)
  }

  /** Call discover after signing so we capture and save the account
   *  Note: This is needed for Ethereum since we dont know a public key until we sign with an account
   */
  async callDiscoverAfterSign(transactionData: TransactionData) {
    const { chainNetwork, account } = transactionData
    const { provider } = transactionData?.signOptions || {}
    const walletProvider = Helpers.mapAuthProviderToWalletType(provider)
    const discoverOptions: DiscoverOptions = {
      walletType: walletProvider,
      chainNetwork,
      oreAccount: account,
    }
    await this.discover(discoverOptions)
  }

  /** Returns true if network is NOT an EOS sisterchain */
  async isNotEosNetwork(chainNetwork: ChainNetwork) {
    const networkSetting = await this._oreIdContext.getChainNetworkSettings(chainNetwork)
    return !(networkSetting.type === ChainPlatformType.eos || networkSetting.type === ChainPlatformType.ore)
  }

  // Supported features by provider

  /** whether discovery is supported by the provider */
  canDiscover(walletType: ExternalWalletType) {
    if (this.hasTransitProvider(walletType)) {
      return getTransitProviderAttributes(walletType).supportsDiscovery
    }
    return false
  }

  /** whether signString is supported by the provider */
  canSignString(walletType: ExternalWalletType) {
    if (this.hasTransitProvider(walletType)) {
      return getTransitProviderAttributes(walletType).supportsSignArbitrary
    }
    return false
  }

  /** whether call to discover is required by provider before login */
  requiresDiscoverToLogin(walletType: ExternalWalletType) {
    if (this.hasTransitProvider(walletType)) {
      return getTransitProviderAttributes(walletType).requiresDiscoverToLogin
    }
    return false
  }

  /** whether call to logout then login is required by provider before discover */
  requiresLogoutLoginToDiscover(walletType: ExternalWalletType) {
    if (this.hasTransitProvider(walletType)) {
      return getTransitProviderAttributes(walletType).requiresLogoutLoginToDiscover
    }
    return false
  }

  /** default path index for provider (if any) */
  defaultDiscoveryPathIndexList(walletType: ExternalWalletType): number[] {
    if (this.hasTransitProvider(walletType)) {
      return getTransitProviderAttributes(walletType)?.defaultDiscoveryPathIndexList
    }
    return null
  }

  /** help text displayed to user for provider */
  helpTextForProvider(walletType: ExternalWalletType) {
    if (this.hasTransitProvider(walletType)) {
      return getTransitProviderAttributes(walletType).helpText
    }

    return null
  }
}
export class Observable<Subscriber extends Function> {
  private _subscribers: any[]

  constructor() {
    this._subscribers = []
  }

  public subscribe(subscriber: Subscriber) {
    const hasThisSubscriber = this._subscribers.find(s => s === subscriber)
    if (!subscriber || hasThisSubscriber) {
      return
    }
    subscriber(this)
    this._subscribers.push(subscriber)
  }

  public unsubscribe(subscriber: Subscriber) {
    this._subscribers = this._subscribers.filter(f => f !== subscriber)
  }

  public callSubscribers() {
    this._subscribers.forEach(f => f(this))
  }
}
import { ApiGetUserParams, callApiGetUser, callApiPasswordLessSendCode, callApiPasswordLessVerifyCode } from '../api'
import { callApiAddPermission } from '../api/endpoints/addPermission'
import OreIdContext from '../core/IOreidContext'
import { getOreIdNewChainAccountUrl } from '../core/urlGenerators'
import Helpers from '../utils/helpers'
import { Observable } from '../utils/observable'
import { AuthProvider, AccountName, ChainAccount, ChainNetwork, ExternalWalletType } from '../common/models'
import { NewAccountOptions, NewAccountWithOreIdResult } from '../core/models'
import {
  UserChainAccount,
  UserData,
  UserPermissionData,
  UserPermissionForChainAccount,
  UserSourceData,
  WalletPermission,
} from './models'
import { AccessTokenHelper } from '../auth/accessTokenHelper'

const { isNullOrEmpty } = Helpers

export type SubscriberUser = (values: User) => void

export class User extends Observable<SubscriberUser> {
  constructor(args: { oreIdContext: OreIdContext; accessTokenHelper: AccessTokenHelper; accountName: AccountName }) {
    super()
    this._oreIdContext = args.oreIdContext
    this._accessTokenHelper = args.accessTokenHelper // reference to current accessToken (via getter)
    this._accountName = args.accountName
    this._accessTokenHelper.subscribe(this.onUpdateAccessTokenHelper)
  }

  // pulled from the accessToken
  private _accountName: AccountName

  private _accessTokenHelper: AccessTokenHelper

  private _oreIdContext: OreIdContext

  /** User's basic information and blockchain accounts (aka permissions) */
  private _userSourceData: UserSourceData

  private get accessToken(): string {
    return this._accessTokenHelper.accessToken
  }

  /** User's OreID (accountName) */
  get accountName(): AccountName {
    return this._accountName
  }

  /** User's personal info (e.g. name, email, picture) */
  get data(): UserData {
    this.assertUserHasData()
    const { permissions, ...otherInfo } = this._userSourceData
    return {
      ...otherInfo,
      chainAccounts: this.getChainAccounts(),
    }
  }

  /** Whether user's data has been retrieved with getData() */
  get hasData() {
    return !isNullOrEmpty(this?._userSourceData)
  }

  private setUserSourceData(userSourceData: UserSourceData) {
    this._userSourceData = userSourceData
    super.callSubscribers()
  }

  /** Return Blockchain accounts associated with the user's OreId account */
  private getChainAccounts(): UserChainAccount[] {
    this.assertUserHasData()
    const chainAccounts = (this._userSourceData.permissions || []).map(perm => {
      const [defaultPermission] = this.getDefaultPermissionForChainAccount(perm.chainAccount, perm.chainNetwork)
      return {
        chainAccount: perm.chainAccount,
        chainNetwork: perm.chainNetwork,
        defaultPermission,
        permissions: this.getPermissionForChainAccount(perm.chainAccount, perm.chainNetwork),
      }
    })
    // only return unique combinations of chainAccount and chainNetwork
    return Helpers.getUniqueValues(chainAccounts)
  }

  /** Whether we have a valid access token for the current user */
  get isLoggedIn(): boolean {
    return !!this.accessToken
  }

  /** runs when accessTokenHelper changes */
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  private onUpdateAccessTokenHelper = (newAccessTokenHelper: AccessTokenHelper) => {
    super.callSubscribers()
  }

  /** throw if user data hasn't been retrieved yet */
  private assertUserHasData() {
    if (!this.hasData) {
      throw new Error('User data hasnt been retrieved. Call user.getData() first.')
    }
  }

  /** throw if user hasn't have a valid email (i.e. user.email) */
  private assertUserHasValidEmail() {
    this.assertUserHasData()
    const { email } = this?.data || {}
    if (!Helpers.isValidEmail(email)) throw new Error('User doesnt have a valid email')
  }

  /** Get the user info from ORE ID API for a given user account and (usually) save the user into localStorage 'cache'
   *  Must have a valid accessToken to retrieve user
   */
  async getData(forceRefresh?: boolean): Promise<UserData> {
    if (this.hasData && !forceRefresh) return this.data
    // eslint-disable-next-line prefer-destructuring
    const accessToken = this.accessToken
    if (!accessToken) {
      throw new Error('AccessToken is missing or has expired')
    }
    // get account specified in access token
    const account = this?._accountName
    const params: ApiGetUserParams = { account }
    const userSourceData = await callApiGetUser(this._oreIdContext, params)

    this._accountName = account
    this.setUserSourceData(userSourceData)
    return this.data
  }

  /** Clears user's accessToken and user profile data */
  logout() {
    this._oreIdContext.logout()
  }

  /** Request OREID to create a new blockchain account in an existing user's wallet
   *  This is an advanced feature - it most cases, blockchain accounts will be created automatically upon first login
   */
  // async newChainAccount(newAccountOptions: NewAccountOptions) {
  //   // TODO - call API to create chain account (requires apiKey with createAccount right)
  //   // for webwidget, it should just refresh user info upon callback
  // }

  /** Returns a fully formed url to redirect the user's browser to create a new chain account using ORE ID
   *  This function calls the /new-account web endpoint
   *  Returns: Callback returns new chainAccount name */
  async getNewChainAccountUrl(newAccountOptions: NewAccountOptions): Promise<NewAccountWithOreIdResult> {
    const { account, accountType, chainNetwork, accountOptions, provider, state } = newAccountOptions || {}
    const { newAccountCallbackUrl, backgroundColor } = this._oreIdContext.options
    const args = {
      account,
      accountType,
      backgroundColor,
      chainNetwork,
      accountOptions,
      provider,
      callbackUrl: newAccountCallbackUrl,
      state,
    }
    const newAccountUrl = await getOreIdNewChainAccountUrl(this._oreIdContext, args)
    return { newAccountUrl, errors: null }
  }

  /** Send a code to the user's primary email (user.email) - in order to verify the user has access to it
   *  After sending the code, use checkVerificationCodeForEmail() to verify that the user received the code */
  async sendVerificationCodeToEmail() {
    this.assertUserHasValidEmail()
    const result = await callApiPasswordLessSendCode(this._oreIdContext, {
      email: this?.data?.email,
      provider: AuthProvider.Email,
    })
    return result
  }

  /** Confirm that the code matches the last one just sent to the email by sendVerificationCodeToEmail() */
  async checkVerificationCodeForEmail({ code }: { code: string }) {
    this.assertUserHasValidEmail()
    const result = await callApiPasswordLessVerifyCode(this._oreIdContext, {
      code,
      email: this?.data?.email,
      provider: AuthProvider.Email,
    })
    return result
  }

  /** Map permission from server data to local UserPermission object */
  mapUserPermission(permission: UserPermissionData): UserPermissionForChainAccount {
    if (isNullOrEmpty(permission)) return null
    const { chainNetwork, chainAccount, permission: permissionName, ...other } = permission
    return {
      ...other,
      name: permissionName,
    }
  }

  /** returns the UserPermissins a chainNetwork/chainAccount
   * if defaultOnly = true, returns the single default permission for the chainAccount
   */
  getPermissionForChainAccount(
    chainAccount: ChainAccount,
    chainNetwork: ChainNetwork,
  ): UserPermissionForChainAccount[] {
    const accountPermissions = this._userSourceData.permissions.filter(
      p => p.chainAccount === chainAccount && p.chainNetwork === chainNetwork,
    )
    return accountPermissions.map(this.mapUserPermission)
  }

  getDefaultPermissionForChainAccount(
    chainAccount: ChainAccount,
    chainNetwork: ChainNetwork,
  ): UserPermissionForChainAccount[] {
    const accountPermissions = this.getPermissionForChainAccount(chainAccount, chainNetwork)
    let defaultPermission = accountPermissions.filter(p => p.isDefault === true)
    // if no default is defined, and there is only one permission, use it as the default - this might be an external key
    if (isNullOrEmpty(defaultPermission) && accountPermissions?.length === 1) {
      defaultPermission = accountPermissions
    }
    return defaultPermission
  }

  /** Update permissions for user's ORE Account if any */
  async updatePermissionsIfNecessary(args: {
    chainAccount: ChainAccount
    permissions: WalletPermission[]
    chainNetwork: ChainNetwork
    walletType: ExternalWalletType
  }) {
    // use logged-in account if missing oreAccount param
    await this.addWalletPermissionsToOreIdAccount(args)
  }

  // for each permission in the wallet, add to ORE ID (if not in user's record)
  /** for all walletPermissions not already in the user's OreID wallet, add it by calling the api (addPermission) */
  async addWalletPermissionsToOreIdAccount(args: {
    chainAccount: ChainAccount
    chainNetwork: ChainNetwork
    permissions: WalletPermission[]
    walletType: ExternalWalletType
  }) {
    const { chainAccount, chainNetwork, permissions, walletType } = args
    if (!this.accountName || isNullOrEmpty(permissions) || isNullOrEmpty(chainNetwork)) {
      return // todo: consider if we should exit silently here - since we are called after discovery everytime, then answer is probably yes
    }

    // get latest user info
    await this.getData()

    // for each permission provided, check if it's already in the user's list, if not, add it by calling the api (addPermission)
    await Helpers.asyncForEach(permissions, async perm => {
      const permission = perm.name
      let parentPermission = perm.parent
      if (!parentPermission) {
        // HACK: assume parent permission - its missing from the discover() results
        parentPermission = 'active'

        if (permission === 'owner') {
          parentPermission = ''
        } else if (permission === 'active') {
          parentPermission = 'owner'
        }
      }
      // filter out permission that the user already has in his record
      const skipThisPermission = this._userSourceData.permissions.some(
        up =>
          (up.chainAccount === chainAccount && up.chainNetwork === chainNetwork && up.permission === permission) ||
          permission === 'owner',
      )

      // don't add 'owner' permission and skip ones that are already stored in user's account
      if (skipThisPermission !== true) {
        // let publicKey = p.required_auth.keys[0].key; //TODO: Handle multiple keys and weights
        const { publicKey } = perm
        // if call is successful, nothing is returned in response (except processId)
        await callApiAddPermission(this._oreIdContext, {
          account: this.accountName,
          chainAccount,
          chainNetwork,
          publicKey,
          parentPermission,
          permission,
          provider: walletType,
        })
      }
    })

    // reload user to get updated permissions
    await this.getData()
  }
}
import Helpers from '../utils/helpers'
import { JWTToken } from './models'
import { Observable } from '../utils/observable'

export type SubscriberAccessTokenHelper = (accessTokenHelper: AccessTokenHelper) => void

export class AccessTokenHelper extends Observable<SubscriberAccessTokenHelper> {
  constructor(accessToken?: string, ignoreIssuer = false) {
    super()
    this._ignoreIssuer = ignoreIssuer
    this.setAccessToken(accessToken)
  }

  _accessToken: string

  _decodedAccessToken: JWTToken

  _idToken: string

  _decodedIdToken: JWTToken

  _ignoreIssuer: boolean

  get accessToken() {
    return this._accessToken
  }

  get decodedAccessToken() {
    if (!this.accessToken) return null
    return this._decodedAccessToken
  }

  get idToken() {
    return this._idToken
  }

  get decodedIdToken() {
    return this._decodedIdToken
  }

  get accountName() {
    if (!this.accessToken) return null
    AccessTokenHelper.assertIsTokenValid(this.decodedAccessToken)
    return Helpers.getClaimFromJwtTokenBySearchString(this.decodedAccessToken, 'https://oreid.aikon.com/account')
  }

  /** clear accessToken */
  clearAccessToken() {
    this.setAccessToken(null)
  }

  /** clear idToken */
  clearIdToken() {
    this.setIdToken(null)
  }

  /** Whether current accessToken is expired (or is missing)
   *  (optional) provide a Date() to compare expiration against - defaults to current Date()
   *  Returns: (boolean) true if hasExpired
   */
  hasExpired(now?: Date): boolean {
    if (!this._decodedAccessToken) return true
    if (!AccessTokenHelper.isTokenDateValidNow(this._decodedAccessToken, now)) {
      return true
    }
    return false
  }

  setAccessToken(value: string) {
    if (!value) {
      this._accessToken = null // allows clearing of value
    } else {
      const decodedAccessToken = Helpers.jwtDecodeSafe(value) as JWTToken
      if (!decodedAccessToken) throw Error(`Can't set accessToken. Value provided: ${value}`)
      AccessTokenHelper.assertIsTokenValid(decodedAccessToken, this._ignoreIssuer)
      AccessTokenHelper.assertIdTokenMatchesAccessToken(decodedAccessToken, this.decodedIdToken)
      this._decodedAccessToken = decodedAccessToken
      this._accessToken = value
      // Set a timer to clear the token when it expires (will clear if already expired)
      Helpers.runAtTime(() => {
        this.clearAccessToken()
        console.log('accessToken has expired and has been cleared')
      }, this._decodedAccessToken.exp * 1000)
    }
    super.callSubscribers()
  }

  setIdToken(value: string) {
    if (!value) {
      this._decodedIdToken = null // allows clearing of value
    } else {
      const decodedIdToken = Helpers.jwtDecodeSafe(value) as JWTToken
      if (!decodedIdToken) throw Error(`Can't set IdToken. Value provided: ${value}`)
      AccessTokenHelper.assertIsTokenValid(decodedIdToken, this._ignoreIssuer)
      AccessTokenHelper.assertIdTokenMatchesAccessToken(this.decodedAccessToken, decodedIdToken)
      this._decodedIdToken = decodedIdToken
      this._idToken = value
      // Set a timer to clear the token when it expires (will clear if already expired)
      Helpers.runAtTime(() => {
        this.clearIdToken()
        console.log('idToken has expired and has been cleared')
      }, this._decodedIdToken.exp * 1000)
    }
    super.callSubscribers()
  }

  /** Throws if accessToken is NOT set yet */
  assertHasAccessToken() {
    if (!this.accessToken) throw Error('AccessToken not set. Login first.')
  }

  /** Whether token is a valid OREID issued token and NOT expired */
  static isTokenValid(decodedToken: Partial<JWTToken>, ignoreIssuer = false): boolean {
    try {
      AccessTokenHelper.assertIsTokenValid(decodedToken, ignoreIssuer)
      return true
    } catch (error) {
      return false
    }
  }

  /** Throws if decodedToken is NOT a valid OREID issued token */
  static assertIsTokenValid(decodedToken: Partial<JWTToken>, ignoreIssuer = false) {
    const now = new Date()
    if (!decodedToken) throw Error('JWT (access or id) token is invalid, or expired)')
    // check if ORE ID issued this token
    if (!ignoreIssuer && !decodedToken.iss.includes('oreid.io')) {
      throw Error('Access token not issued by ORE ID')
    }
    if (!AccessTokenHelper.isTokenDateValidNow(decodedToken, now)) {
      throw Error('Access token has expired')
    }
  }

  /** Throws if accessToken does not match the same user and issuer as the idToken */
  static assertIdTokenMatchesAccessToken(decodedAccessToken: Partial<JWTToken>, decodedIdToken: Partial<JWTToken>) {
    if (!decodedAccessToken || !decodedIdToken) return
    // check if ORE ID issued this token
    if (decodedAccessToken.iss !== decodedIdToken.iss) {
      throw Error('AccessToken and IdToken mismatch - not issued by the same issuer')
    }
    if (decodedAccessToken.sub !== decodedIdToken.sub) {
      throw Error('AccessToken and IdToken mismatch - not for the same user')
    }
  }

  /** Whether a JWT token is valid for use right now - not expired and not being used before first usable date (nbf) */
  static isTokenDateValidNow(decodedToken: Partial<JWTToken>, now?: Date): boolean {
    const nowDate = now || new Date()
    const nowInMs = nowDate.getTime() / 1000
    if (typeof decodedToken.exp !== 'undefined' && decodedToken?.exp < nowInMs) {
      return false
    }
    if (typeof decodedToken?.nbf !== 'undefined' && decodedToken?.nbf > nowInMs) {
      return false
    }
    return true
  }
}
import {
  ApiConvertOauthTokensParams,
  ApiLoginUserWithTokenParams,
  ApiMessageResult,
  ApiNewUserWithTokenParams,
  callApiConvertOauthTokens,
  callApiLoginUserWithToken,
  callApiNewUserWithToken,
} from '../api'
import { providersNotImplemented } from '../constants'
import OreIdContext from '../core/IOreidContext'
import { getOreIdAuthUrl } from '../core/urlGenerators'
import TransitHelper from '../transit/TransitHelper'
import { User } from '../user/user'
import Helpers from '../utils/helpers'
import LocalState from '../utils/localState'
import { Observable } from '../utils/observable'
import { AccessTokenHelper } from './accessTokenHelper'
import { AuthResult, LoginWithOreIdResult } from '../core/models'
import { LoginOptions, LoginWithTokenOptions, LoginWithWalletOptions, NewUserWithTokenOptions } from './models'

export type SubscriberAuth = (auth: Auth) => void

export class Auth extends Observable<SubscriberAuth> {
  constructor(args: { oreIdContext: OreIdContext }) {
    super()
    this._oreIdContext = args.oreIdContext
    this._localState = this._oreIdContext.localState
    this._transitHelper = new TransitHelper({ oreIdContext: this._oreIdContext, user: this._user })
    this.initAccessTokenHelper()
  }

  private _accessTokenHelper: AccessTokenHelper

  private _localState: LocalState

  private _oreIdContext: OreIdContext

  private _transitHelper: TransitHelper

  private _user: User

  /** User's OreID (accountName) */
  get accessTokenHelper(): AccessTokenHelper {
    return this._accessTokenHelper
  }

  private initAccessTokenHelper() {
    this._accessTokenHelper = new AccessTokenHelper()
    const savedToken = this._localState?.accessToken
    this.accessToken = savedToken
    this.saveAccessTokenAndNotifySubscribers(savedToken) // if savedToken is expired, it will be not set here
    // listen for future changes to accessTokenHelper
    this._accessTokenHelper.subscribe(this.onUpdateAccessTokenHelper)
  }

  /** User's OreID (accountName) */
  get accountName(): string {
    return this._accessTokenHelper?.accessToken ? this._accessTokenHelper?.accountName : null
  }

  get idToken() {
    return this._accessTokenHelper?.idToken
  }

  /** retrieve accessToken saved in local storage - is automatically deleted when token expires */
  get accessToken() {
    return this._accessTokenHelper?.accessToken
  }

  /** Sets the access token in local storage (and in accessTokenHelper)
   * this token will be used to call ORE ID APIs (on behalf of the user)
   * This token is user-specific - call logout to clear it upon user log-out
   * When the accessToken token expires, it will be deleted from local storage and user will be cleared
   */
  set accessToken(accessToken: string) {
    try {
      // decodes and validates accessToken is a valid token
      // if incoming token has expired, _accessTokenHelper will throw (and token wont be saved)
      this._accessTokenHelper.setAccessToken(accessToken)
      // NOTE: We dont save the access token to local storage here as that will happen when onUpdateAccessTokenHelper is called
    } catch (error) {
      console.log('Cant set accessToken.', error.message)
    }
  }

  /** set private variable and save to localState
   * NOTE: This is called every time this._accessTokenHelper.accessToken changes (or expires)
   */
  private saveAccessTokenAndNotifySubscribers(accessToken: string) {
    if (this._localState?.accessToken !== accessToken) {
      this._localState.saveAccessToken(accessToken)
      this._user = null
    }
    super.callSubscribers()
  }

  /** Returns user object matching current accessToken
   *  A newly created object object won't have user.info populated, call user.getData() to retrieve
   */
  get user() {
    if (!this._user) {
      this._user = new User({
        oreIdContext: this._oreIdContext,
        accessTokenHelper: this._accessTokenHelper, // accessToken helper
        accountName: this.accountName, // accountName
      })
    }
    return this._user
  }

  /** We have a valid access token for the current user */
  get isLoggedIn(): boolean {
    return !!this.accessToken
  }

  /** runs when accessTokenHelper changes */
  // eslint-disable-next-line @typescript-eslint/no-unused-vars
  private onUpdateAccessTokenHelper = (newAccessTokenHelper: AccessTokenHelper) => {
    // save new access token
    this.saveAccessTokenAndNotifySubscribers(this._accessTokenHelper.accessToken)
  }

  private clearAccessToken() {
    // clear accessToken and user
    this._localState.clearAccessToken()
    this._accessTokenHelper.clearAccessToken()
    super.callSubscribers()
  }

  /** Calls the 'connect' function on a external wallet (e.g. Metamask)
   *  For most, however, this function returns the chainAccount selected by the user in the wallet app
   *  Different wallets have different behavior. Some do not support this feature. */
  async connectWithWallet(loginOptions: LoginWithWalletOptions) {
    const { provider } = loginOptions

    if (!this._transitHelper.isTransitProvider(provider) || providersNotImplemented.includes(provider)) {
      throw new Error(`loginWithWallet not supprted for provider: ${provider}`)
    }

    return this.connectToWalletProvider(loginOptions)
  }

  /** Connect to the wallet provider
   *  For some wallet types, this will include an unlock and 'login' flow to select a chain account
   *  If a chainAccount is selected, it and it's associated publicKey (if available) will be saved to the user's OreId wallet as an 'external key' */
  private async connectToWalletProvider(loginOptions: LoginWithWalletOptions) {
    const { provider } = loginOptions
    if (this._transitHelper.isTransitProvider(provider)) {
      return this._transitHelper.loginWithTransitProvider(loginOptions)
    }
    throw new Error(`Not a valid External Wallet provider: ${provider}`)
  }

  /** Calls the account/convert-oauth api
   * Converts OAuth tokens from some 3rd-party source to OREID Oauth tokens
   * The third-party (e.g. Auth0 or Google) must be registered in the AppRegistration.oauthSettings
   * Returns: OreId issued accessToken and idToken
   * */
  private async convertOauthTokens(oauthOptions: ApiConvertOauthTokensParams) {
    return callApiConvertOauthTokens(this._oreIdContext, oauthOptions)
  }

  /**
   * Converts OAuth accessToken or idToken from some 3rd-party source (e.g. Google) to OreId OAuth accessToken
   * The third-party (e.g. Auth0 or Google) must be registered in the App Registration's oauthSettings
   * If a user does not curently exist that matches the info in the incoming idToken, an error is thrown
   * Does not requires a user to be logged-in (no current accessToken) or apiKey
   * Returns: OreId issued accessToken
   * */
  async loginWithToken(loginOptions: LoginWithTokenOptions): Promise<LoginWithOreIdResult> {
    if (!loginOptions?.idToken && !loginOptions?.accessToken) {
      throw new Error('Cant loginWithToken - missing required parameter: accessToken OR idToken')
    }
    const { accessToken, error, processId } = await this.loginWithJwtToken(loginOptions)
    if (!error) {
      this.accessToken = accessToken // saves in cache and in local storage
      this.user.getData()
    }
    return { accessToken, errors: error, processId }
  }

  /** Converts OAuth idToken from some 3rd-party source to OREID Oauth accessTokens
   * The third-party (e.g. Auth0 or Google) must be registered in the AppRegistration.oauthSettings
   * Creates a new OreId user and account from info in the incoming idToken
   * If a matching user already exist, and error is returned
   * Requires a valid idToken but no current accessToken or apiKey
   * Returns: OreId issued accessToken
   * */
  async newUserWithToken(loginOptions: NewUserWithTokenOptions): Promise<LoginWithOreIdResult> {
    const { idToken } = loginOptions || {}
    if (!idToken) {
      throw new Error('Cant do newUserWithToken - missing required parameter: idToken')
    }
    const { accessToken, error, processId } = await this.newAccountWithIdToken({ idToken })
    if (!error) {
      this.accessToken = accessToken // saves in cache and in local storage
      this.user.getData()
    }
    return { accessToken, errors: error, processId }
  }

  /** Calls api account/login-user-with-token for loginWithToken() (after checking for valid token */
  private checkJwtTokenAndReturnError(jwtTokenString: string): { error?: string; message?: string } {
    try {
      // eslint-disable-next-line @typescript-eslint/no-unused-vars
      const accessTokenHelper = new AccessTokenHelper(jwtTokenString, true)
    } catch (error) {
      return {
        error: 'token_invalid',
        message: 'token invalid or corrupt - must be a JWT Oauth2 token',
      }
    }

    return null
  }

  /** Calls api account/login-user-with-token for loginWithToken() (after checking for valid token */
  private async loginWithJwtToken(
    oauthOptions: ApiLoginUserWithTokenParams,
  ): Promise<{ accessToken: string } & ApiMessageResult> {
    const { accessToken, idToken } = oauthOptions
    // check valid idToken
    let tokenCheckError = accessToken ? this.checkJwtTokenAndReturnError(accessToken) : null
    if (tokenCheckError) return { accessToken: null, ...tokenCheckError }
    // check valid accessToken
    tokenCheckError = idToken ? this.checkJwtTokenAndReturnError(idToken) : null
    if (tokenCheckError) return { accessToken: null, ...tokenCheckError }

    const response = await callApiLoginUserWithToken(this._oreIdContext, oauthOptions)
    if (!response?.errorCode) {
      this.setAuthResult({ accessToken: response?.accessToken })
    }
    return {
      accessToken: response.accessToken,
      error: response?.errorCode,
      message: response?.errorMessage,
      processId: response?.processId,
    }
  }

  /** Calls api account/new-user-with-token for newUserWithToken() (after checking for valid token */
  private async newAccountWithIdToken(
    oauthOptions: ApiNewUserWithTokenParams,
  ): Promise<{ accessToken: string } & ApiMessageResult> {
    const { idToken } = oauthOptions
    // check valid ifToken
    const tokenCheckError = idToken ? this.checkJwtTokenAndReturnError(idToken) : null
    if (tokenCheckError) return { accessToken: null, ...tokenCheckError }

    const response = await callApiNewUserWithToken(this._oreIdContext, oauthOptions)
    if (!response?.errorCode) {
      this.setAuthResult({ accessToken: response?.accessToken })
    }
    return {
      accessToken: response.accessToken,
      error: response?.errorCode,
      message: response?.errorMessage,
      processId: response?.processId,
    }
  }

  /** clear accessToken and user */
  logout() {
    this.clearAccessToken()
    this._user = null
  }

  /** Returns a fully formed url to redirect the user's browser to login using ORE ID
   *  This function calls the /auth web endpoint
   *  Returns: Callback returns account, and optionally accessToken and/or idToken for user */
  async getLoginUrl(loginOptions: LoginOptions): Promise<LoginWithOreIdResult> {
    const { code, email, idToken, phone, provider, state, linkToAccount, returnAccessToken, returnIdToken } =
      loginOptions || {}
    const { authCallbackUrl, backgroundColor } = this._oreIdContext.options
    const args = {
      code,
      email,
      idToken,
      phone,
      provider,
      backgroundColor,
      callbackUrl: authCallbackUrl,
      state,
      linkToAccount,
      returnAccessToken: Helpers.isNullOrEmpty(returnAccessToken) ? true : returnAccessToken, // if returnAccessToken not specified, default to true
      returnIdToken,
    }
    const loginUrl = await getOreIdAuthUrl(this._oreIdContext, args)
    return { loginUrl, errors: null }
  }

  /** Extracts and returns the response parameters on the /auth callback URL string
   *  Applies accessToken and idToken (if included on the url) to local state
   */
  handleAuthCallback(callbackUrlString: string): AuthResult {
    // Parses error codes and returns an errors array
    // (if there is an error_code param sent back - can have more than one error code - seperated by a ‘&’ delimeter
    const {
      access_token: accessToken,
      account,
      id_token: idToken,
      errors,
      process_id: processId,
      state,
    } = Helpers.extractDataFromCallbackUrl(callbackUrlString)
    const response: AuthResult = { account }

    if (errors) response.errors = errors
    if (processId) response.processId = processId
    if (state) response.state = state
    if (accessToken) response.accessToken = accessToken
    if (idToken) response.idToken = idToken

    if (!errors) {
      this.setAuthResult(response)
    }

    // clear the busy indicator now that we've finsihed the auth flow
    this._oreIdContext.setIsBusy(false)
    return response
  }

  /** store response from auth flow (accountName, accessToken, idToken) in localState */
  setAuthResult(authResponse: AuthResult) {
    const { accessToken, idToken } = authResponse
    if (!accessToken) throw Error('Cant setAuthResult. accessToken is missing')
    if (idToken) this._accessTokenHelper.setIdToken(null) // clear the existing idToken first (so set accessToken wont throw a mismatch when set)
    this.accessToken = accessToken // saves the token to localstorage
    if (idToken) {
      this._accessTokenHelper.setIdToken(idToken)
    }
  }
}
import OreIdContext from '../core/IOreidContext'
import {
  ApiKeyUsedFor,
  AuthProvider,
  CreateTransactionData,
  ExternalWalletType,
  SignWithOreIdResult,
  TransactionData,
} from '../models'
import TransitHelper from '../transit/TransitHelper'
import {
  assertHasApiKey,
  callApiCanAutosignTransaction,
  callApiCustodialSignTransaction,
  callApiSignTransaction,
} from '../api'
import { getOreIdSignUrl } from '../core/urlGenerators'
import Helpers from '../utils/helpers'
import { User } from '../user/user'

export default class Transaction {
  constructor(args: { oreIdContext: OreIdContext; user: User; data: TransactionData }) {
    this._oreIdContext = args.oreIdContext
    this._user = args.user
    this.assertValidTransactionAndSetData(args.data)
    this._transitHelper = new TransitHelper({ oreIdContext: this._oreIdContext, user: this._user })
  }

  private _oreIdContext: OreIdContext

  private _data: TransactionData

  private _transitHelper: TransitHelper

  private _user: User

  get data() {
    return this._data
  }

  /** ensure all required parameters are provided */
  assertValidTransactionAndSetData(createTransactionData: CreateTransactionData) {
    const { chainNetwork, transaction, signedTransaction } = createTransactionData || {}
    const missingFields: string[] = []
    const validationIssues: string[] = []

    if (!this._user || !this._user.hasData) {
      throw new Error('Make sure that a user has been authenticated and that youve called user.getData()')
    }

    // required fields
    if (!chainNetwork) missingFields.push('chainNetwork')
    if (!transaction && !signedTransaction) missingFields.push('transaction OR signedTransaction')
    // validaton rules
    if (!this._user.accountName)
      validationIssues.push('Transaction Data error - Expecting a user.accountName - is the user logged-in in?')
    if (transaction && signedTransaction) validationIssues.push('Only provide one: transaction OR signedTransaction')

    // TODO: Additional validation
    // transaction OR signedTransaction - check for valid JSON object

    if (!Helpers.isNullOrEmpty(missingFields)) {
      throw new Error(`Transaction parameter(s) missing: ${missingFields.join(', ')}`)
    }
    if (!Helpers.isNullOrEmpty(validationIssues)) {
      throw new Error(`Transaction parameter error: ${validationIssues.join(', ')}`)
    }

    this.setTransactionData(createTransactionData)
  }

  private setTransactionData(createTransactionData: CreateTransactionData) {
    // account param is set to logged-in user
    this._data = {
      account: this._user.accountName,
      ...createTransactionData,
    }
    // set encoded transaction properties
    if (createTransactionData?.transaction)
      this._data.encodedTransaction = Helpers.base64Encode(JSON.stringify(createTransactionData.transaction))
    if (createTransactionData?.signedTransaction)
      this._data.encodedSignedTransaction = Helpers.base64Encode(
        JSON.stringify(createTransactionData.signedTransaction),
      )
  }

  /** ensure that the chainNetwork and chainAccount for the transaction are in the user's wallet
   *  NOTE: This check is not required for a user signing with a wallet app - Since the account may be in the wallet and not yet added to OreId
   */
  assertTransactionAccountValidForUser() {
    const { chainAccount, chainNetwork } = this._data
    const { accountName } = this._user

    const chainAccountsInWallet = this._user?.data?.chainAccounts?.find(
      perm => perm.chainNetwork === chainNetwork && perm.chainAccount === chainAccount,
    )

    const allPermissionsExternal = chainAccountsInWallet?.permissions?.every(p => p.privateKeyStoredExterally === true)
    const externalWalletType = chainAccountsInWallet?.permissions?.find(
      p => p.privateKeyStoredExterally === true,
    )?.externalWalletType

    if (!chainAccountsInWallet) {
      throw new Error(
        `Can't find a chainAccount: ${chainAccount} for chainNetwork: ${chainNetwork} in user's oreId account: ${accountName}`,
      )
    }

    if (allPermissionsExternal) {
      throw new Error(
        `ChainAccount: ${chainAccount} for chainNetwork: ${chainNetwork} appears to be in a wallet app (type: ${externalWalletType}) - use signWithWallet() to sign`,
      )
    }
  }

  // TODO: check user.chainAccounts that

  /** validates that transaction is well-formed for the blockcahin
   * Returns array of errors
   */
  async validate(): Promise<string[]> {
    // TODO: add transactin/validate api endpoint
    throw new Error('Not Implemented')
  }

  /**
   * Returns a url to redirect the user's browser to - to sign transaction using OREID web interface
   */
  async getSignUrl(): Promise<SignWithOreIdResult> {
    this.assertTransactionAccountValidForUser()
    const transactionData = this.data
    const { signCallbackUrl } = this._oreIdContext.options
    // eslint-disable-next-line no-param-reassign
    if (!transactionData?.signOptions) transactionData.signOptions = {}
    // eslint-disable-next-line no-param-reassign
    transactionData.signOptions.callbackUrl = signCallbackUrl
    const signUrl = await getOreIdSignUrl(this._oreIdContext, transactionData)
    return { signUrl, errors: null }
  }

  /**
   * Whether the provided transaction (or signedTransaction) can be autoSigned via api (without user interaction)
   * Requires an apiKey with the autoSign right
   * Returns: true if transaction can be signed using tansaction.sign()
   * */
  async checkCanAutoSign() {
    let autoSignCredentialsExist: boolean
    try {
      assertHasApiKey(this._oreIdContext, ApiKeyUsedFor.AutoSigning, '')
      // this will throw if we don't have an api key with the right rights
      ;({ autoSignCredentialsExist } = await callApiCanAutosignTransaction(this._oreIdContext, this._data))
    } catch (error) {
      return false // can't auto-sign
    }
    return autoSignCredentialsExist
  }

  /** Attempt to sign a transaction without user interaction
   *  Expects user to have previously approved autoSign for transaction type and it hasn't expired
   *  Call callApiCanAutosignTransaction() first to confirm that this transaction can be autoSigned before attempting this call
   */
  async autoSign() {
    this.assertTransactionAccountValidForUser()
    const transactionData = this.data
    const { processId, signedTransaction, transactionId, errorCode, errorMessage } = await callApiSignTransaction(
      this._oreIdContext,
      { transactionData, autoSign: true },
    )

    if (errorCode || errorMessage) throw new Error(errorMessage)
    return { processId, signedTransaction, transactionId }
  }

  /** Sign a transaction without user interaction
   *  Requires a user's wallet password or encrypted password (a 'custodial' account managed by you)
   *  Requires an apiKey with the proxySign right
   */
  async signWithPassword(userPassword?: string, userPasswordEncrypted?: string) {
    this.assertTransactionAccountValidForUser()
    const transactionData = this.data
    if (!transactionData?.signOptions) transactionData.signOptions = {}
    if (userPassword) transactionData.signOptions.userPassword = userPassword
    if (userPasswordEncrypted) transactionData.signOptions.userPasswordEncrypted = userPasswordEncrypted

    if (transactionData.signOptions.userPassword && transactionData.signOptions.userPasswordEncrypted) {
      throw new Error('Provide either a userPassword OR userPasswordEncrypted param. Both were provided.')
    }

    const { processId, signedTransaction, transactionId, errorCode, errorMessage } =
      await callApiCustodialSignTransaction(this._oreIdContext, { transactionData, autoSign: false })
    if (errorCode || errorMessage) throw new Error(errorMessage)
    return { processId, signedTransaction, transactionId }
  }

  /** Sign with a supported blockchain wallet via Transit provider */
  async signWithWallet(walletType: ExternalWalletType) {
    const transactionData = this.data
    const isTransitProvider = this._transitHelper.isTransitProvider(walletType)
    if (!isTransitProvider) return null
    const signResult = this._transitHelper.signWithTransitProvider(transactionData, walletType)
    // If we've signed a transaction with a key in a wallet, callDiscoverAfterSign() will add it to the user's wallet
    const provider = Helpers.toEnumValue(AuthProvider, walletType)
    const { account, chainNetwork } = transactionData
    await this._transitHelper.callDiscoverAfterSign({ account, chainNetwork, signOptions: { provider } })
    return signResult
  }
}
import HmacSHA256 from 'crypto-js/hmac-sha256'
import Axios from 'axios'
import Helpers from './helpers'

export function generateHmac(secret: string, data: string) {
  const hmac = HmacSHA256(data, secret)
  return hmac.toString()
}

/** Generate HMAC for data string
 *  data param can be stringified object
 *  If apiKey is not provided, expects useProxyServer:true and a proxy server to be available to generate the hmac
 *  Expects proxyserver /oreid/hmac to host an endpoint to sign with the apiKey and return the hmac
 */
export async function generateHmacWithApiKeyOrProxyServer(useProxyServer: boolean, apiKey: string, data: string) {
  if (!useProxyServer && Helpers.isNullOrEmpty(apiKey)) {
    throw new Error('Cant generate hmac. apiKey missing or empty - its required if not using a proxy server')
  }

  let hmac: string
  if (useProxyServer) {
    try {
      // get an hmac from the proxy server endpoint (since it has the secret apiKey)
      const response = await Axios.post('/oreid/hmac', JSON.stringify({ data }), {
        headers: { 'Content-Type': 'application/json' },
      })
      hmac = response?.data?.hmac
    } catch (networkError) {
      const error = this.getErrorFromAxiosError(networkError)
      throw error
    }
  } else {
    hmac = generateHmac(apiKey, data)
  }
  return hmac
}

/** Generate HMAC for url string and append it to end or url e.g. http:/nnnnnn&hmac=xxx */
export async function appendHmacToUrl(useProxyServer: boolean, apiKey: string, url: string) {
  if (Helpers.isNullOrEmpty(url)) {
    throw new Error('Cant generate hmac. url missing or empty')
  }

  const hmac = await generateHmacWithApiKeyOrProxyServer(useProxyServer, apiKey, url)

  const urlEncodedHmac = encodeURIComponent(hmac)
  // correct hmac already in place
  if (url.includes(`&hmac=${urlEncodedHmac}`)) {
    return url
  }
  return `${url}&hmac=${urlEncodedHmac}`
}
import { UserSourceData } from '../user/models'
import IStorage from '../core/IStorage'

export default class LocalState {
  constructor(appId: string, storage: IStorage) {
    this.appId = appId
    this.cachedaccessToken = null
    this.cachedUser = null
    this.storage = storage
  }

  appId: string

  cachedaccessToken: string

  cachedUser: UserSourceData

  storage: IStorage

  accessTokenKey() {
    return `oreid.${this.appId}.accessToken`
  }

  get accessToken(): string {
    if (!this.cachedaccessToken) this.loadAccessToken()
    return this.cachedaccessToken
  }

  loadAccessToken() {
    this.cachedaccessToken = this.storage.getItem(this.accessTokenKey())
  }

  saveAccessToken(accessToken: string) {
    if (accessToken) {
      this.cachedaccessToken = accessToken
      this.storage.setItem(this.accessTokenKey(), accessToken)
    } else {
      this.clearAccessToken()
    }
  }

  clearAccessToken() {
    this.cachedaccessToken = null
    this.storage.removeItem(this.accessTokenKey())
  }

  clear() {
    this.clearAccessToken()
  }
}
